package com.thetesttribeproject;import org.openqa.selenium.By;import org.openqa.selenium.Keys;import org.openqa.selenium.WebDriver;import org.openqa.selenium.WebElement;import org.openqa.selenium.chrome.ChromeDriver;import org.openqa.selenium.chrome.ChromeOptions;public class OptionsTest {    public static void main(String[] args) throws InterruptedException {        ChromeOptions options= new ChromeOptions();        options.addArguments("--incognito");        WebDriver driver = new ChromeDriver(options);        driver.manage().window().maximize();        driver.navigate().to("https://google.com");        System.out.println(driver.getCurrentUrl());        WebElement googleImages= driver.findElement(By.xpath("//a[@data-pid = '2']"));        googleImages.click();        Thread.sleep(2000);        WebElement SearchTxtBox = driver.findElement(By.xpath("//textarea[@aria-label = 'Search']"));        SearchTxtBox.sendKeys("goku dbz", Keys.ENTER);        Thread.sleep(5000);        driver.quit();    }}